section.codeblock:
type AuthSource {
   id: ID!
   name: String
   type: AuthSourceType! 
   redirectUris: [String!]
}

enum AuthSourceType {
   OPENID
   SAML
   BASIC
   JWT
   BASIC_GATEWAY
}

interface AuthenticationSource {
    id: ID!
    name: String!
    description: String
    roles: [AuthSourceRole]
}

type AuthSourceRole {
    id: ID!
    name: String!
    description: String
}

type AuthSourceJWT implements AuthenticationSource {
    id: ID!
    name: String!
    description: String
    roles: [AuthSourceRole]
    issuer: String!
    jwksUrl: String
    groupsAttribute: String
    audiences: [String!]
    issuerError: IssuerError
}

type IssuerError {
    code: IssuerErrorCode!
    detail: String!
}

input AuthSourceJWTCreateInput {
    name: String!
    issuer: String!
    details: AuthSourceJWTDetailsInput
}

input AuthSourceJWTDetailsInput {
    description: String
    groupsAttribute: String
    roles: [String!]
    audiences: [String!]
}

enum IssuerErrorCode {
    MISSING_JWKS
    URL_INVALID
    UNKNOWN_HOST
    COULD_NOT_PARSE_CONFIG
    REQUEST_TIMEOUT
    REMOTE_HOST_RESPONDED_WITH_ERROR
}

input AuthSourceJWTUpdateInput {
    id: ID!
    name: String
    issuer: String
    details: AuthSourceJWTDetailsInput
}

li.codeblock:
query {
    authSources {
        id
        name
        type
        redirectUris
    }
}

ul.li:

Update operationWhen using the update operation, specifying an
                    empty or null array will remove any redirect URIs that may have been previously
                    set. If that happens, the authentication source will use the wildcard default
                    for allowed URIs. The URI inputs must be valid URIs that do not contain any
                    fragments. The host name in the URI should be in lower case. A single wildcard
                    character is allowed in the URI, but it must be located at the end. If the
                    developer portal capability of the Gateway is also being used, additional URIs
                    should be added to the list for the assigned Authentication Source so that it
                    accepts login attempts from the developer portal.

                        Request:mutation {
   authSourceRedirectUrisUpdate(authSourceId: "<xxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxx>", redirectUris: [ "https://biz.co:18077/*" ]) {
       id
       name
       redirectUris
   }
}

Response:{
   "data": {
       "authSourceRedirectUrisUpdate": {
           "name": "BizCo Authentication Source",
           "type": "OPENID",
           "redirectUris": [
               "https://biz.co:18077/*"
           ]
       }
   }
}

li.codeblock:
mutation authSourceJWTCreate($authSourceJWT:AuthSourceJWTCreateInput!) {
    authSourceJWTCreate(
        authSourceJWT:$authSourceJWT
    )
    {
        id
        name
        description
        issuer
        jwksUrl
        groupsAttribute
        roles {
            id
            name
        }
        audiences
        issuerError {
            code
            detail
        }
    }
}

mutation authSourceJWTUpdate($authSourceJWT:AuthSourceJWTUpdateInput!) {
    authSourceJWTUpdate(
        authSourceJWT:$authSourceJWT
    )
    {
        id
        name
        description
        issuer
        jwksUrl
        groupsAttribute
        roles {
            id
            name
        }
        audiences
        issuerError {
            code
            detail
        }
    }
}
